$( document ).ready(function() {
    $( ".container" ).css( "padding", "50px" );
    console.log("done");
    var buttons = document.getElementsByClassName("btn-primary");
    console.log(buttons);
    for (var i = 0; i < buttons.length; i++)  {
        buttons[i].innerHTML = "Speichern";
    }

    // hack to have default unchecked
    var checked = $('[data-fieldname="teilnahme_rundgang"]').find('input');
    checked.prop("checked", false);

    // load the event from the url argument
    get_arguments();
});

function get_arguments() {
    // get command line parameters
    var arguments = window.location.toString().split("?");
    if (!arguments[arguments.length - 1].startsWith("http")) {
        var args_raw = arguments[arguments.length - 1].split("&");
        var args = {};
        args_raw.forEach(function (arg) {
            var kv = arg.split("=");
            if (kv.length > 1) {
                args[kv[0]] = kv[1];
            }
        });

        if (args['event']) {
            var event = $('[data-fieldname="event"]').find('input');
            event.value = args['event'];
            event.readOnly = true;
        }
    }
}
